# Словарь для удобного доступа ко всем чертам
all_traits = {
    "Обычная": common_traits,
    "Редкая": rare_traits,
    "Эпическая": epic_traits,
    "Мифическая": mythical_traits,
    "Легендарная": legendary_traits
}


# --- КОМАНДА /ablrandom ---
# Выдает случайную черту с учетом веса редкости
@bot.message_handler(commands=['ablrandom'])
def get_random_ability(message):
    rarities = ["Обычная", "Редкая", "Эпическая", "Мифическая", "Легендарная"]
    # Шансы, указанные в задаче: 60%, 35%, 3.5%, 1.49%, 0.01%
    weights = [60, 35, 3.5, 0.99, 0.01]

    # Выбираем одну редкость на основе весов
    chosen_rarity = random.choices(rarities, weights=weights)[0]

    # Выбираем случайную черту из выпавшей редкости
    trait = random.choice(all_traits[chosen_rarity])

    # Формируем и отправляем сообщение
    response = f"🎉 Вам выпала черта!\n\n**Редкость:** {chosen_rarity}\n**Черта:** {trait}"
    bot.reply_to(message, response, parse_mode='Markdown')


